import { Component, OnInit } from '@angular/core';
import { SharedService } from '../service/shared.service'
import { QuizService } from '../service/quiz-service/quiz.service'
import { Observable, lastValueFrom } from 'rxjs';
import { Question } from '../model/Question';
import { QuizResponse } from '../model/QuizResponse';



@Component({
  selector: 'app-quiz',
  templateUrl: './quiz.component.html',
  styleUrls: ['./quiz.component.css']
})
export class QuizComponent implements OnInit {

  category: string = "";
  difficulty: string = "";
  quiz: QuizResponse;
  questions: Question[];
  counter:number=0;
  currentQuestion:Question;
  correctAnswers:number=0;
  answers:String[];

  constructor(private quizService:QuizService,private sharedService: SharedService) { 


  }

   getQuiz() {
    this.category = this.sharedService.getCategory();
    this.difficulty = this.sharedService.getDifficulty();
    this.quizService.getQuiz(10,0,this.category, this.difficulty).subscribe((quiz:QuizResponse)=>{

        this.quiz=quiz;
        this.questions=quiz.questions;
        this.currentQuestion=this.questions[0];
        this.answers.concat(this.currentQuestion.correctAnswer);
        this.answers.concat(this.currentQuestion.incorrectAnswers);
    });

  }
  nextQuestion()
  {
    this.answers=[];
    this.counter++;
    this.currentQuestion=this.questions[this.counter];
    this.answers.concat(this.currentQuestion.correctAnswer);
    this.answers.concat(this.currentQuestion.incorrectAnswers);

  }


  async ngOnInit():Promise<void> {
    await this.getQuiz();
    console.log(this.quiz);
    console.log(this.questions);
  }

}
